public class Star1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int cnt=5;
		for(int i=1;i<=cnt;i++)
		{
			for(int j=1;j<=i;j++) {
				System.out.print("*" );
			}
			System.out.println("  ");
			System.out.println("  ");
		}

	}

}
